import { Rule, SchematicContext, Tree, url, apply,template, mergeWith } from '@angular-devkit/schematics';
import { Schema } from './schema';
import {  strings } from '@angular-devkit/core';
import { classify, dasherize } from '@angular-devkit/core/src/utils/strings';


// You don't have to export the function as default. You can also have more than one rule factory
// per file.
export function pvp(_options: Schema): Rule {

  return (tree: Tree, _context: SchematicContext) => {

    const sourceTemplates=url('./files');
    const sourceParametrizedTemplates=apply(sourceTemplates,[template({
      ..._options,
      ...strings,
      classIt,
      kebabIt
    })
  ]);
console.log(tree);

function classIt(value:string)
{
return classify(value);
}
function kebabIt(value:string)
{
return dasherize(value);
}

  return mergeWith(sourceParametrizedTemplates);
    // const { name } = _options;
    // tree.create(`${name}.js`, 'hi');
    // return tree;
  };
}

